package Day3;

public class Even_Or_Odd_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=14325;
		
		if(num%2 == 0)
		{
		System.out.println("Number is even number");	
		}
		else
		{
		System.out.println("Number is odd number");	
		}
		
		
		
	}

}
